import React, { Component } from "react";
import { Card, Row, Col, Button } from "antd";

class OrderBook extends Component {
	constructor(props) {
		super(props);

		this.state = {
			buyOrders: [],
			count: 0,
			buyOrdersArray: [],
		};
	}

	componentDidMount = () => {
		const { drizzle, drizzleState } = this.props;
		const buyOrderCount = drizzle.contracts.CelebrityExchange.methods[
			"buyOrderCount"
		].cacheCall();
		this.setState({
			buyOrderCount,
		});
		// const count =
		// 	drizzleState.contracts.CelebrityExchange.buyOrderCount[
		// 		this.state.buyOrderCount
		// 	];
		// console.log(count && count.value);
		// for (let i = 0; i < (count && count.value); i++) {
		// 	console.log(count && count.value);
		// 	let buyOrder = this.props.drizzle.contracts.CelebrityExchange.methods[
		// 		"buyOrders"
		// 	].cacheCall(i);
		// 	console.log(buyOrder);
		// 	this.setState({ buyOrders: [...this.state.buyOrders, buyOrder] });
		// }
	};

	render() {
		const { CelebrityExchange } = this.props.drizzleState.contracts;

		const count = CelebrityExchange.buyOrderCount[this.state.buyOrderCount];
		// let buyOrdersArray = [];
		// for (let i = 0; i < (count && count.value); i++) {
		// 	let tempOrder = CelebrityExchange.buyOrders[this.state.buyOrders[i]];
		// 	tempOrder = tempOrder && tempOrder.value;

		// 	buyOrdersArray.push(tempOrder);
		// }
		return (
			<div className="">
				<Card className="orderBook">
					<Row>
						<Col span="12">
							<p
								style={{
									textAlign: "center",
									color: "white",
									fontSize: "12",
								}}
							>
								Open Buy Orders
							</p>
							<table style={{ width: "95%", textAlign: "right" }}>
								<thead>
									<tr>
										<th>Volume</th>
										<th>Buy Price</th>
									</tr>
								</thead>
								<tbody>
									<tr>
										<td>1000</td>
										<td style={{ color: "#55bd6c" }}>0.0034</td>
									</tr>
									<tr>
										<td>500</td>
										<td style={{ color: "#55bd6c" }}>0.0032</td>
									</tr>
								</tbody>
							</table>
						</Col>
						<Col span="12">
							<p
								style={{
									textAlign: "center",
									color: "white",
									fontSize: "12",
								}}
							>
								Open Sell Orders
							</p>
							<table style={{ width: "100%" }}>
								<thead>
									<tr>
										<th>Sell Price</th>
										<th>Volume</th>
									</tr>
								</thead>
								<tbody>
									<tr>
										<td style={{ color: "#f1432f" }}>0.0035</td>
										<td>1200</td>
									</tr>
									<tr>
										<td style={{ color: "#f1432f" }}>0.0040</td>
										<td>120</td>
									</tr>
								</tbody>
							</table>
						</Col>
					</Row>
					<div>Count: {count && count.value}</div>
					{/* <div>Orders: {this.getOrders()}</div> */}
					Order:{" "}
					{buyOrdersArray.map((order) => {
						<div>{order}</div>;
					})}
					{/* <div>Count: {buyOrdersArray}</div> */}
				</Card>
			</div>
		);
	}
}

export default OrderBook;
