import React, { useState, useEffect } from "react";
import { Card } from "antd";

export default function Trades(props) {
	// To provide as dependency for useEffect
	const events = props.drizzleState.contracts["CelebrityExchange"].events;
	const [completedTrades, setCompletedTrades] = useState([]);
	useEffect(() => {
		const web3 = props.drizzle.web3;
		const contract = props.drizzle.contracts["CelebrityExchange"];
		const yourContractWeb3 = new web3.eth.Contract(
			contract.abi,
			contract.address
		);
		let completedBuyOrders = [];
		let completedSellOrders = [];
		yourContractWeb3
			.getPastEvents("BuyOrderPlaced", {
				fromBlock: 0,
				toBlock: "latest",
			})
			.then(function (events) {
				events.map((event) => {
					completedBuyOrders.push(event.returnValues);
				});
			});
		console.log(completedBuyOrders);
		yourContractWeb3
			.getPastEvents("SellOrderPlaced", {
				fromBlock: 0,
				toBlock: "latest",
			})
			.then(function (events) {
				events.map((event) => {
					completedSellOrders.push(1);
				});
			});
		console.log(completedSellOrders);
		setCompletedTrades(completedBuyOrders.concat(completedSellOrders));
		// eslint-disable-next-line react-hooks/exhaustive-deps
	}, [props.drizzle.contracts["CelebrityExchange"], events]);
	return (
		<div>
			<Card className="trades">
				<p
					style={{
						textAlign: "center",
						color: "white",
						fontSize: "12",
					}}
				>
					Trades
				</p>
				<table style={{ width: "100%", textAlign: "center" }}>
					<thead>
						<tr>
							<th>Price</th>
							<th>Volume</th>
							<th>Time</th>
						</tr>
					</thead>
					<tbody>
						<tr>
							<td style={{ color: "#55bd6c" }}>0.0032</td>
							<td>1200</td>
							<td>few seconds ago</td>
						</tr>
						<tr>
							<td style={{ color: "#f1432f" }}>0.0029</td>
							<td>950</td>
							<td>25 days ago</td>
						</tr>
						<tr>
							<td style={{ color: "#55bd6c" }}>0.0035</td>
							<td>120</td>
							<td>21.14.03</td>
						</tr>
					</tbody>
				</table>
			</Card>
		</div>
	);
}
