import React, { useState, useEffect } from "react";

export default function SingleBuyOrder(props) {
	const [buyOrderDetailsArray, setBuyOrderDetailsArray] = useState([
		{ id: null, tokensLeftToBuy: 1, buyPrice: 1 },
	]);
	useEffect(() => {
		let buyOrdersArray = [];
		for (let i = 0; i < props.count; i++) {
			let tempBuyOrderDetails =
				props.drizzleState.contracts.CelebrityExchange.buyOrders[
					props.orders[i]
				];
			tempBuyOrderDetails = tempBuyOrderDetails && tempBuyOrderDetails.value;
			buyOrdersArray.push(tempBuyOrderDetails);
		}
		handleStatusChange(buyOrdersArray);
	}, [
		props.drizzleState.contracts.CelebrityExchange.buyOrders,
		props.orders,
		props.count,
	]);
	function handleStatusChange(_buyOrdersArray) {
		setBuyOrderDetailsArray(_buyOrdersArray);
	}
	return (
		<React.Fragment>
			{typeof buyOrderDetailsArray[0].id != null
				? buyOrderDetailsArray.map((order) => {
						return (
							<tr key={typeof order != "undefined" ? order.id : null}>
								<td>
									{typeof order != "undefined"
										? order.tokensLeftToBuy / 10 ** 18
										: null}
								</td>
								<td style={{ color: "#55bd6c" }}>
									{typeof order != "undefined"
										? order.buyPrice / 10 ** 18
										: null}
								</td>
							</tr>
						);
				  })
				: null}
		</React.Fragment>
	);
}
