import React, { useState, useEffect } from "react";
import { Card, Table } from "antd";

export default function UserOrders(props) {
	const [openFocused, setOpenFocus] = useState(true);
	const [allOpenOrders, setAllOpenOrders] = useState([]);
	useEffect(() => {
		setOpenFocus(openFocused);

		let tempAllOpenOrders = props.buyOrders
			.concat(props.sellOrders)
			.filter((order) =>
				typeof order != "undefined"
					? order.status == 0 || order.status == 1
					: null
			)
			.sort((trade1, trade2) => trade2.timestamp - trade1.timestamp);

		setAllOpenOrders(tempAllOpenOrders);
	}, [openFocused, props.buyOrders, props.sellOrders]);
	const changeStatus = (e) => {
		setOpenFocus(e.target.value == "OPEN" ? true : false);
	};
	const columns = [
		{
			title: "Celebrity",
			dataIndex: "id",
			width: "25%",
		},
		{
			title: "Tokens",
			dataIndex: "tokens",
			width: "25%",
		},
		{
			title: "Price",
			dataIndex: "price",
		},
		{
			title: "% Filled",
			dataIndex: "tokensLeft",
		},
	];
	const dataForDisplay = allOpenOrders.map((order, index, array) => {
		if (typeof order != "undefined") {
			let tokens =
				order.tokenBuy != "undefined"
					? order.tokenBuy / 10 ** 18
					: order.tokenSell / 10 ** 18;

			let price =
				order.buyPrice != "undefined"
					? order.buyPrice / 10 ** 18
					: order.sellPrice / 10 ** 18;

			let tokensLeft =
				order.tokensLeftToBuy != "undefined"
					? 100 - (order.tokensLeftToBuy / order.tokenBuy) * 100
					: 100 - (order.tokensLeftToSell / order.tokenSell) * 100;

			// array[index].tokenBuy = tokenBuy;
			// array[index].tokenSell = tokenSell;
			// array[index].buyPrice = buyPrice;
			// array[index].sellPrice = sellPrice;
			// array[index].tokensLeftToBuy = tokensLeftToBuy;
			// array[index].tokensLeftToSell = tokensLeftToSell;
			array[index].tokens = tokens;
			array[index].price = price;
			array[index].tokensLeft = tokensLeft;
		}
	});

	return (
		<section>
			{/* buy order seperate component, sell order seperate, load dynamically */}
			<div
				style={{ position: "absolute", top: "5px", zIndex: "5", left: "5px" }}
			>
				<input
					type="radio"
					id="OPEN"
					name="openFocus"
					value="OPEN"
					onChange={(value) => changeStatus(value)}
					checked={openFocused}
				/>
				<label
					for="OPEN"
					style={{
						borderTop: `${
							openFocused ? "5px solid #f9de37" : "5px solid gray"
						}`,
					}}
				>
					OPEN ORDERS
				</label>
				<input
					type="radio"
					id="COMPLETED"
					name="completedFocus"
					value="COMPLETED"
					onChange={(value) => changeStatus(value)}
					checked={!openFocused}
				/>
				<label
					for="COMPLETED"
					style={{
						borderTop: `${
							!openFocused ? "5px solid #55bd6c" : "5px solid gray"
						}`,
					}}
				>
					HISTORY
				</label>
			</div>
			<Card className="userOrders" style={{ overflow: "hidden" }}>
				<div
					style={{
						height: "45vh",
						overflow: "auto",
						position: "absolute",
						top: "70px",
						display: "table",
					}}
				>
					{typeof allOpenOrders[0] != "undefined"
						? allOpenOrders.map((order) => {
								return (
									<React.Fragment>
										<Table
											columns={columns}
											dataSource={allOpenOrders}
											pagination={{ pageSize: 50 }}
											scroll={{ y: 240 }}
										/>
									</React.Fragment>
								);
						  })
						: null}
				</div>
			</Card>
		</section>
	);
}
