import React, { useState, useEffect } from "react";
import SingleBuyOrder from "./SingleBuyOrder";

export default function BuyOrderBook(props) {
	const [buyOrders, setBuyOrders] = useState([{ id: 0 }]);
	useEffect(() => {
		const { drizzle } = props;
		let buyOrders = [];
		for (let i = 0; i < props.buyOrderCount; i++) {
			let buyOrder = drizzle.contracts.CelebrityExchange.methods[
				"buyOrders"
			].cacheCall(i);
			buyOrders.push(buyOrder);
		}
		handleStatusChange(buyOrders);
	});
	function handleStatusChange(_buyOrders) {
		const { CelebrityExchange } = props.drizzleState.contracts;
		let buyOrderDetails = [];
		for (let i = 0; i < props.count; i++) {
			let tempBuyOrderDetails = CelebrityExchange.buyOrders[props.buyOrders[i]];
			buyOrderDetails.push(tempBuyOrderDetails && tempBuyOrderDetails.value);
		}

		setBuyOrders(buyOrderDetails);
	}
	return (
		<React.Fragment>
			{console.log(buyOrders)}
			{buyOrders.map((order) => {
				return (
					<SingleBuyOrder
						drizzle={props.drizzle}
						drizzleState={props.drizzleState}
						key={order.id}
						order={order.id}
					/>
				);
			})}
		</React.Fragment>
	);
}
