import React, { useState, useEffect } from "react";
import SingleBuyOrder from "./SingleBuyOrder";

export default function BuyOrderBook(props) {
	const [buyOrders, setBuyOrders] = useState([{ id: 0 }]);
	useEffect(() => {
		let count =
			props.drizzleState.contracts.CelebrityExchange.buyOrderCount[
				props.buyOrderCount
			];
		let buyOrders = [];
		for (let i = 0; i < (count && count.value); i++) {
			let buyOrder = props.drizzle.contracts.CelebrityExchange.methods[
				"buyOrders"
			].cacheCall(i);
			buyOrders.push(buyOrder);
		}
		handleStatusChange(buyOrders);
	}, [
		props.drizzleState.contracts.CelebrityExchange.buyOrderCount,
		props.drizzle.contracts.CelebrityExchange,
		props.buyOrderCount,
	]);
	function handleStatusChange(_buyOrders) {
		setBuyOrders(_buyOrders);
	}
	return (
		<React.Fragment>
			return (
			<SingleBuyOrder
				drizzle={props.drizzle}
				drizzleState={props.drizzleState}
				key={buyOrders}
				order={buyOrders}
			/>
			);
		</React.Fragment>
	);
}
