import React, { Component } from "react";

class BuyOrderBook extends Component {
	state = { buyOrdersArray: [] };
	static getDerivedStateFromProps(props, state) {
		const { CelebrityExchange } = props.drizzleState.contracts;

		const count = CelebrityExchange.buyOrderCount[this.props.count];
		let buyOrdersArray = [];

		for (let i = 0; i < (count && count.value); i++) {
			let tempOrder = CelebrityExchange.buyOrders[this.props.buyOrders[i]];
			tempOrder = tempOrder && tempOrder.value;

			buyOrdersArray.push(tempOrder);
		}
		return { buyOrdersArray: buyOrdersArray };
		// this.state = {
		// 	buyOrdersArray: buyOrdersArray,
		// };
	}
	render() {
		return (
			<div>
				<p>
					{/* {this.state.buyOrdersArray.map((event) => {
						<p>{event}</p>;
					})} */}
					{this.state.buyOrdersArray}
				</p>
			</div>
		);
	}
}
export default BuyOrderBook;
