import React, { Component, useState, useEffect } from "react";
import { Card, Row, Col, Button } from "antd";
import BuyOrderBook from "./BuyOrderBook";

export default function Orderbook(props) {
	const [buyOrderCount, setBuyOrderCount] = useState(0);
	useEffect(() => {
		const { drizzle } = props;
		const buyOrderCount = drizzle.contracts.CelebrityExchange.methods[
			"buyOrderCount"
		].cacheCall();
		handleStatusChange(buyOrderCount);
	}, [props]);
	function handleStatusChange(_buyOrderCount) {
		// setBuyOrderCount(_buyOrderCount);
	}
	return (
		<React.Fragment>
			<Card className="orderBook">
				<Row>
					<Col span="12">
						<p
							style={{
								textAlign: "center",
								color: "white",
								fontSize: "12",
							}}
						>
							Open Buy Orders
						</p>

						<table
							style={{
								width: "95%",
								textAlign: "right",
							}}
						>
							<thead>
								<tr>
									<th>Volume</th>
									<th>Buy Price</th>
								</tr>
							</thead>
						</table>
						<div style={{ height: "240px", overflow: "auto" }}>
							<table
								style={{
									width: "95%",
									textAlign: "right",
								}}
							>
								<tbody>
									{/* <BuyOrderBook
										drizzle={props.drizzle}
										drizzleState={props.drizzleState}
										buyOrderCount={buyOrderCount}
									/> */}
								</tbody>
							</table>
						</div>
					</Col>
					<Col span="12">
						<p
							style={{
								textAlign: "center",
								color: "white",
								fontSize: "12",
							}}
						>
							Open Sell Orders
						</p>
						<table style={{ width: "100%" }}>
							<thead>
								<tr>
									<th>Sell Price</th>
									<th>Volume</th>
								</tr>
							</thead>
							<tbody>
								<tr>
									<td style={{ color: "#f1432f" }}>0.0035</td>
									<td>1200</td>
								</tr>
								<tr>
									<td style={{ color: "#f1432f" }}>0.0040</td>
									<td>120</td>
								</tr>
							</tbody>
						</table>
					</Col>
				</Row>
			</Card>
		</React.Fragment>
	);
}
