import React, { useState, useEffect } from "react";

export default function SingleBuyOrder(props) {
	const [buyOrderDetails, setBuyOrderDetails] = useState([{ id: 0 }]);
	useEffect(() => {
		const { CelebrityExchange } = props.drizzleState.contracts;
		let buyOrderDetails = CelebrityExchange.buyOrders[props.order];
		buyOrderDetails = buyOrderDetails && buyOrderDetails.value;

		handleStatusChange(buyOrderDetails);
	}, [props]);
	function handleStatusChange(_buyOrderDetails) {
		setBuyOrderDetails(_buyOrderDetails);
	}
	return <div>{buyOrderDetails.id}</div>;
}
