import React, { Component } from "react";
import SingleOrder from "./SingleOrder";

class BuyOrderBook extends Component {
	state = { loading: true, buyOrdersArray: [] };
	static getDerivedStateFromProps(props, state) {
		const { CelebrityExchange } = props.drizzleState.contracts;

		const count = CelebrityExchange.buyOrderCount[props.count];
		let buyOrdersArray = [];

		for (let i = 0; i < (count && count.value); i++) {
			let tempOrder = CelebrityExchange.buyOrders[props.buyOrders[i]];
			tempOrder = tempOrder && tempOrder.value;

			buyOrdersArray.push(tempOrder);
		}
		if (buyOrdersArray !== []) {
			return { buyOrdersArray: buyOrdersArray, loading: false };
		}

		// this.state = {
		// 	buyOrdersArray: buyOrdersArray,
		// };
	}
	render() {
		return (
			<div>
				<SingleOrder buyOrdersArray={this.state.buyOrdersArray} />
			</div>
		);
	}
}
export default BuyOrderBook;
