import React, { Component } from "react";
import { Card, Row, Col, Button } from "antd";
import BuyOrderBook from "./BuyOrderBook";

class OrderBook extends Component {
	constructor(props) {
		super(props);
		const { drizzle, drizzleState } = this.props;
		const buyOrderCount = drizzle.contracts.CelebrityExchange.methods[
			"buyOrderCount"
		].cacheCall();

		let buyOrders = [];

		for (let i = 0; i < 18; i++) {
			let buyOrder = this.props.drizzle.contracts.CelebrityExchange.methods[
				"buyOrders"
			].cacheCall(i);

			buyOrders.push(buyOrder);
		}

		this.state = {
			buyOrders: buyOrders,
			count: buyOrderCount,
			buyOrdersArray: [],
		};
	}

	componentDidMount = () => {};

	render() {
		return (
			<div className="">
				<Card className="orderBook">
					<Row>
						<Col span="12">
							<p
								style={{
									textAlign: "center",
									color: "white",
									fontSize: "12",
								}}
							>
								Open Buy Orders
							</p>
							<table style={{ width: "95%", textAlign: "right" }}>
								<thead>
									<tr>
										<th>Volume</th>
										<th>Buy Price</th>
									</tr>
								</thead>
								<tbody>
									<tr>
										<td>1000</td>
										<td style={{ color: "#55bd6c" }}>0.0034</td>
									</tr>
									<tr>
										<td>500</td>
										<td style={{ color: "#55bd6c" }}>0.0032</td>
									</tr>
								</tbody>
							</table>
						</Col>
						<Col span="12">
							<p
								style={{
									textAlign: "center",
									color: "white",
									fontSize: "12",
								}}
							>
								Open Sell Orders
							</p>
							<table style={{ width: "100%" }}>
								<thead>
									<tr>
										<th>Sell Price</th>
										<th>Volume</th>
									</tr>
								</thead>
								<tbody>
									<tr>
										<td style={{ color: "#f1432f" }}>0.0035</td>
										<td>1200</td>
									</tr>
									<tr>
										<td style={{ color: "#f1432f" }}>0.0040</td>
										<td>120</td>
									</tr>
								</tbody>
							</table>
						</Col>
					</Row>
					<BuyOrderBook
						buyOrders={this.state.buyOrders}
						count={this.state.count}
					/>
					{/* <div>Count: {buyOrdersArray}</div> */}
				</Card>
			</div>
		);
	}
}

export default OrderBook;
