import React, { Component } from "react";
import { Card, Input, Button, Form } from "antd";

const layout = {
	labelCol: { span: 0 },
	wrapperCol: { offset: 2, span: 20 },
};

class PlaceOrders extends Component {
	submitOrder = (e) => {
		const _price = document.getElementById("price").value;
		const _amount = document.getElementById("amount").value;
		const _total = document.getElementById("total").value;
		const { drizzle, drizzleState } = this.props;
		const contract = drizzle.contracts.CelebrityExchange;

		// let drizzle know we want to call the `set` method with `value`
		const stackId = contract.methods["placeInitialSellOrder"].cacheSend(
			"0xDD1BcCD8da45D24823a85B5841Aa9861Cc8df301",
			drizzle.web3.utils.toWei(_amount),
			drizzle.web3.utils.toWei(_total),
			{
				from: drizzleState.accounts[0],
			}
		);

		// save the `stackId` for later reference
		this.setState({ stackId });
	};
	render() {
		return (
			<section>
				{/* buy order seperate component, sell order seperate, load dynamically */}
				<div
					style={{ position: "absolute", top: "5px", zIndex: "5", left: "5px" }}
				>
					<button type="button" className="buyButton">
						BUY
					</button>
					<button type="button" className="sellButton">
						SELL
					</button>
				</div>
				<Card className="placeOrders">
					<div
						style={{
							textAlign: "center",
							position: "absolute",
							top: "70px",
							width: "100%",
						}}
					>
						<Form {...layout} style={{ width: "100%" }}>
							<Form.Item
								name="price"
								id="price"
								label="PRICE"
								rules={[{ required: true }]}
							>
								<Input
									autoComplete="off"
									addonBefore="PRICE"
									suffix="ETH"
									defaultValue=""
								/>
							</Form.Item>
							<Form.Item
								name="amount"
								label="AMOUNT"
								id="price"
								rules={[{ required: true }]}
							>
								<Input
									autoComplete="off"
									addonBefore="AMOUNT"
									suffix="TOKENS"
									defaultValue=""
								/>
							</Form.Item>
							<Form.Item
								name="total"
								label="TOTAL"
								id="total"
								rules={[{ required: true }]}
							>
								<Input
									autoComplete="off"
									addonBefore="TOTAL"
									suffix="ETH"
									defaultValue=""
								/>
							</Form.Item>

							<Button
								type="primary"
								htmlType="submit"
								onClick={() => this.submitOrder()}
							>
								Submit
							</Button>
						</Form>
					</div>
				</Card>
			</section>
		);
	}
}

export default PlaceOrders;
