	render() {
		return (
			<React.Fragment>
				<Row>
					<Col span={6}>
						<TokensList />
					</Col>
					<Col span={12}>
						<Row>
							<Col span="24">
								<PriceChart />
							</Col>
							<Col span="24">
								<Row>
									<Col span="14">
										<OrderBook
											drizzle={this.props.drizzle}
											drizzleState={this.props.drizzleState}
											handleBuyOrders={this.handleBuyOrders}
											handleSellOrders={this.handleSellOrders}
										/>
									</Col>
									<Col span="10">
										<Trades
											drizzle={this.props.drizzle}
											drizzleState={this.props.drizzleState}
											buyOrders={this.state.buyOrders}
											sellOrders={this.state.sellOrders}
										/>
									</Col>
								</Row>
							</Col>
						</Row>
					</Col>
					<Col span={6}>
						<Row>
							<Col span={24}>
								<PlaceOrders
									drizzle={this.props.drizzle}
									drizzleState={this.props.drizzleState}
								/>
							</Col>

							<Col span={24}>
								<UserOrders
									drizzle={this.props.drizzle}
									drizzleState={this.props.drizzleState}
									buyOrders={this.state.buyOrders}
									sellOrders={this.state.sellOrders}
								/>
							</Col>
						</Row>
					</Col>
				</Row>
				<section></section>
				<section>
					<AssetStats />
				</section>
				<section></section>
				<section></section>
				<section></section>
