import React, { useState, useEffect } from "react";
import { Card, Table } from "antd";

export default function UserOrders(props) {
	const [openFocused, setOpenFocus] = useState(true);
	const [allOpenOrders, setAllOpenOrders] = useState([]);
	useEffect(() => {
		setOpenFocus(openFocused);

		let tempAllOpenOrders = props.buyOrders
			.concat(props.sellOrders)
			.filter((order) =>
				typeof order != "undefined"
					? order.status == 2 || order.status == 3
					: null
			)
			.sort((trade1, trade2) => trade2.timestamp - trade1.timestamp);
		setAllOpenOrders(tempAllOpenOrders);
	}, [openFocused, props.buyOrders, props.sellOrders]);
	const changeStatus = (e) => {
		setOpenFocus(e.target.value == "OPEN" ? true : false);
	};
	const columns = [
		{
			title: "Celebrity",
			dataIndex: "celebrityToken",
			width: "25%",
		},
		{
			title: "Tokens Ordered",
			dataIndex: "tokenBuy",
			width: "25%",
		},
		{
			title: "Price",
			dataIndex: "buyPrice",
		},
		{
			title: "% Filled",
			dataIndex: "tokensLeftToBuy",
		},
	];
	return (
		<section>
			{/* buy order seperate component, sell order seperate, load dynamically */}
			<div
				style={{ position: "absolute", top: "5px", zIndex: "5", left: "5px" }}
			>
				<input
					type="radio"
					id="OPEN"
					name="openFocus"
					value="OPEN"
					onChange={(value) => changeStatus(value)}
					checked={openFocused}
				/>
				<label
					for="OPEN"
					style={{
						borderTop: `${
							openFocused ? "5px solid #f9de37" : "5px solid gray"
						}`,
					}}
				>
					OPEN ORDERS
				</label>
				<input
					type="radio"
					id="COMPLETED"
					name="completedFocus"
					value="COMPLETED"
					onChange={(value) => changeStatus(value)}
					checked={!openFocused}
				/>
				<label
					for="COMPLETED"
					style={{
						borderTop: `${
							!openFocused ? "5px solid #55bd6c" : "5px solid gray"
						}`,
					}}
				>
					HISTORY
				</label>
			</div>
			<Card className="userOrders" style={{ overflow: "hidden" }}>
				<div style={{ height: "45vh", overflow: "auto" }}>
					<Table
						columns={columns}
						dataSource={allOpenOrders}
						pagination={{ pageSize: 50 }}
						scroll={{ y: 240 }}
					/>
					{typeof allOpenOrders[0] != "undefined"
						? allOpenOrders
								.filter((order) =>
									typeof order != "undefined"
										? order.status == 2 || order.status == 3
										: null
								)
								.sort((trade1, trade2) => trade2.timestamp - trade1.timestamp)
								.map((order) => {
									return (
										<React.Fragment>
											<p key={order.id}>
												{typeof order != "undefined" ? "FC-MSD" : null}
												<br></br>
												{typeof order != "undefined"
													? order.tokenBuy / 10 ** 18
													: null}
												<br></br>
												{typeof order != "undefined"
													? order.buyPrice / 10 ** 18
													: null}
												<br></br>
												{typeof order != "undefined"
													? order.tokensLeftToBuy / 10 ** 18
													: null}
												<br></br>
												{typeof order != "undefined" ? order.status : null}
											</p>
										</React.Fragment>
									);
								})
						: null}
				</div>
			</Card>
		</section>
	);
}
