import React, { Component } from "react";

class SingleBuyOrder extends Component {
	state = { buyOrderDetails: null, loading: true };
	static getDerivedStateFromProps(props, state) {
		const { CelebrityExchange } = props.drizzleState.contracts;

		let buyOrderDetails = CelebrityExchange.buyOrders[props.key];
		buyOrderDetails = buyOrderDetails && buyOrderDetails.value;

		return { buyOrderDetails: buyOrderDetails, loading: false };
	}
	render() {
		return (
			<React.Fragment>
				{typeof this.state.buyOrderDetails != "undefined"
					? this.state.buyOrderDetails.id
					: "Loading"}
			</React.Fragment>
		);
	}
}

export default SingleBuyOrder;
