import React, { Component } from "react";
import { Card, Input, Button, Form } from "antd";

const layout = {
	labelCol: { span: 8 },
	wrapperCol: { span: 14 },
};

class PlaceOrders extends Component {
	submitOrder = (e) => {
		console.log(document.getElementById("price").value);
		console.log(document.getElementById("amount").value);
		console.log(document.getElementById("total").value);
	};
	render() {
		return (
			<section>
				{/* buy order seperate component, sell order seperate, load dynamically */}
				<div
					style={{ position: "absolute", top: "5px", zIndex: "5", left: "5px" }}
				>
					<button type="button" className="buyButton">
						BUY
					</button>
					<button type="button" className="sellButton">
						SELL
					</button>
				</div>
				<Card className="placeOrders">
					<div
						style={{
							textAlign: "center",
							position: "absolute",
							top: "70px",
							width: "100%",
						}}
					>
						<Form {...layout} style={{ width: "100%" }}>
							<Form.Item
								name="price"
								id="price"
								label="PRICE"
								rules={[{ required: true }]}
							>
								<Input />
							</Form.Item>
							<Form.Item
								name="amount"
								label="AMOUNT"
								id="price"
								rules={[{ required: true }]}
							>
								<Input />
							</Form.Item>
							<Form.Item
								name="total"
								label="TOTAL"
								id="total"
								rules={[{ required: true }]}
							>
								<Input />
							</Form.Item>

							<Button
								type="primary"
								htmlType="submit"
								onClick={() => this.submitOrder()}
							>
								Submit
							</Button>
						</Form>
					</div>
				</Card>
			</section>
		);
	}
}

export default PlaceOrders;
