import React from "react";
import { DrizzleContext } from "drizzle-react";
import SetString from "./setString";

class Sample extends React.Component {
	state = { dataKey: null };

	componentDidMount() {
		const { drizzle } = this.props;
		const contract = drizzle.contracts.TokenGenerator;

		// get and save the key for the variable we are interested in
		const dataKey = contract.methods["counter"].cacheCall();
		this.setState({ dataKey });
	}

	render() {
		const { TokenGenerator } = this.props.drizzleState.contracts;
		const storedData = TokenGenerator.counter[this.state.dataKey];
		return <div>Stored Data: {storedData.value}</div>;
	}
}

export default Sample;
