export default function Orderbook(props) {
	const [buyOrderCount, setBuyOrderCount] = useState(0);
	const [count, setCount] = useState(0);
	useEffect(() => {
		const { drizzle, drizzleState } = props;
		const buyOrderCount = drizzle.contracts.CelebrityExchange.methods[
			"buyOrderCount"
		].cacheCall();
		handleStatusChange(buyOrderCount);
	});
	function handleStatusChange(_buyOrderCount) {
		setBuyOrderCount(_buyOrderCount);
		const { drizzle, drizzleState } = props;
		const { CelebrityExchange } = drizzleState.contracts;

		const count = CelebrityExchange.buyOrderCount[buyOrderCount];
		setCount(count && count.value);
	}
	return (
		<React.Fragment>
			<Card className="orderBook">
				<Row>
					<Col span="12">
						<p
							style={{
								textAlign: "center",
								color: "white",
								fontSize: "12",
							}}
						>
							Open Buy Orders
						</p>

						<table
							style={{
								width: "95%",
								textAlign: "right",
							}}
						>
							<thead>
								<tr>
									<th>Volume</th>
									<th>Buy Price</th>
								</tr>
							</thead>
							<tbody>
								<BuyOrderBook
									drizzle={props.drizzle}
									drizzleState={props.drizzleState}
									buyOrderCount={count}
								/>
							</tbody>
						</table>
					</Col>
					<Col span="12">
						<p
							style={{
								textAlign: "center",
								color: "white",
								fontSize: "12",
							}}
						>
							Open Sell Orders
						</p>
						<table style={{ width: "100%" }}>
							<thead>
								<tr>
									<th>Sell Price</th>
									<th>Volume</th>
								</tr>
							</thead>
							<tbody>
								<tr>
									<td style={{ color: "#f1432f" }}>0.0035</td>
									<td>1200</td>
								</tr>
								<tr>
									<td style={{ color: "#f1432f" }}>0.0040</td>
									<td>120</td>
								</tr>
							</tbody>
						</table>
					</Col>
				</Row>
			</Card>
		</React.Fragment>
	);
}
