import React, { Component } from "react";
import SingleBuyOrder from "./SingleBuyOrder";

class BuyOrderBook extends Component {
	state = { loading: false, buyOrders: [] };
	static getDerivedStateFromProps(props, state) {
		const { drizzle, drizzleState } = props;
		const { CelebrityExchange } = drizzleState.contracts;

		const count = CelebrityExchange.buyOrderCount[props.count];

		let buyOrders = [];

		for (let i = 0; i < (count && count.value); i++) {
			let buyOrder = drizzle.contracts.CelebrityExchange.methods[
				"buyOrders"
			].cacheCall(i);

			buyOrders.push(buyOrder);
		}

		return {
			loading: true,
			buyOrders: buyOrders,
		};
	}

	render() {
		return (
			<div>
				{typeof this.state.buyOrders[0] != "undefined"
					? this.state.buyOrders.map((order) => {
							console.log(order);
							return (
								<SingleBuyOrder
									drizzle={this.props.drizzle}
									drizzleState={this.props.drizzleState}
									order={order}
								/>
							);
					  })
					: "Loading"}
			</div>
		);
	}
}
export default BuyOrderBook;
