import React, { Component } from "react";
import "./App.less";
import Sample from "./Sample";
import CelebrityExchangeComponent from "./components/CelebrityExchangeComponent";
import HigherOrderComponent from "./hocs/Layout";

import { DrizzleContext } from "drizzle-react";

class App extends Component {
	componentDidMount = () => {};
	render() {
		return (
			<DrizzleContext.Consumer>
				{(drizzleContext) => {
					const { drizzle, drizzleState, initialized } = drizzleContext;

					const buyOrderCount = drizzle.contracts.CelebrityExchange.methods[
						"buyOrderCount"
					].cacheCall();
					this.setState({
						buyOrderCount,
					});
					const count =
						drizzleState.contracts.CelebrityExchange.buyOrderCount[
							this.state.buyOrderCount
						];
					console.log(count && count.value);
					for (let i = 0; i < (count && count.value); i++) {
						console.log(count && count.value);
						let buyOrder = this.props.drizzle.contracts.CelebrityExchange.methods[
							"buyOrders"
						].cacheCall(i);
						console.log(buyOrder);
						this.setState({ buyOrders: [...this.state.buyOrders, buyOrder] });
					}
					if (!initialized) {
						return "Loading...";
					}

					return (
						<React.Fragment>
							<section className="celebrityExchange">
								<CelebrityExchangeComponent
									drizzle={drizzle}
									drizzleState={drizzleState}
								/>
							</section>
							<Sample drizzle={drizzle} drizzleState={drizzleState} />
						</React.Fragment>
					);
				}}
			</DrizzleContext.Consumer>
		);
	}
}
App = HigherOrderComponent(App);
export default App;
