import React, { useState, useEffect } from "react";
import { Card } from "antd";

export default function UserOrders(props) {
	const [openFocused, setOpenFocus] = useState(true);
	useEffect(() => {
		setOpenFocus(openFocused);
	}, [openFocused]);
	const changeStatus = (e) => {
		setOpenFocus(e.target.value == "OPEN" ? true : false);
	};
	return (
		<section>
			{/* buy order seperate component, sell order seperate, load dynamically */}
			<div
				style={{ position: "absolute", top: "5px", zIndex: "5", left: "5px" }}
			>
				<input
					type="radio"
					id="OPEN"
					name="openFocus"
					value="OPEN"
					onClick={(value) => changeStatus(value)}
					checked={openFocused}
				/>
				<label
					for="OPEN"
					style={{
						borderTop: `${
							openFocused ? "5px solid #55bd6c" : "5px solid gray"
						}`,
					}}
				>
					OPEN ORDERS
				</label>
				<input
					type="radio"
					id="COMPLETED"
					name="completedFocus"
					value="COMPLETED"
					onClick={(value) => changeStatus(value)}
				/>
				<label
					for="COMPLETED"
					style={{
						borderTop: `${
							!openFocused ? "5px solid #f1432f" : "5px solid gray"
						}`,
					}}
				>
					SELL
				</label>
			</div>
			<Card className="userOrders"></Card>
		</section>
	);
}
