import React from "react";
import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { ContractData, ContractForm } from "@drizzle/react-components";
import Sample from "./Sample";
import SetString from "./setString";

const sample = () => (
	<div className="App">
		<ToastContainer />
		<div>
			<h1>Drizzle Event Example</h1>
			<p>
				Connect and react to Solidity Contract events by hooking into Drizzle
				Redux state
			</p>
		</div>

		<div className="section">
			<h2>SimpleStorage with event</h2>
			<p>Change the value to invoke a contract event</p>
			<p>
				<strong>Stored Value: </strong>
				<Sample contract="TokenGenerator" method="counter" />
			</p>
			<SetString contract="TokenGenerator" method="setCounter" />
		</div>
	</div>
);

export default sample;
